contracts/Sales/NFTWithoutPresaleWithRan.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../Base/BaseNFTWithoutPresale.sol";
import "../Base/BaseNFTDelayed.sol";
import "../Base/BaseNFTWithRandom.sol";

contract NFTWithoutPresaleWithRandomDelayed is BaseNFTWithoutPresale, BaseNFTDelayed, BaseNFTWithRandom {
    string public saleType;
    uint256 public version;

    /**
    @notice This is initializer function is used to deploy new sale contract  
    @param _name Collection name  
    @param _symbol Collection symbol
    @param _saleType sale Type  
    @param _baseUri Collection Base URI  
    @param _maxSupply Collection max supply   
    @param _saleId backend sale id   
    @param _publicSaleConfig Public sale configuration data  
    @param _signerAddress Whitelist signer address
    */
    function __NFTWithoutPresaleWithRandomDelayed_init(
        string memory _name,
        string memory _symbol,
        string memory _saleType,
        string memory _baseUri,
        uint256 _maxSupply,
        string memory _saleId,
        publicSaleConfig memory _publicSaleConfig,
        address _signerAddress,
        randomConfig memory _randomConfig
    ) public initializer {
        require(both(_maxSupply > 0, maxSupply <= 100000), "Init: Invalid max supply");
        require(_publicSaleConfig._publicSaleMintCost > 100, "Init: Invalid Token cost");
        require(block.timestamp <= _publicSaleConfig._publicSaleStartTime, "Init: Invalid Start Time");
        require(_publicSaleConfig._publicSaleDuration > 0, "Init: sale duration > 0");
        require(_publicSaleConfig._maxTokenPerMintPublicSale > 0, "Init: Maximum token per mint in public sale > 0");
        require(
            both(
                _publicSaleConfig._maxTokenPerPersonPublicSale >= _publicSaleConfig._maxTokenPerMintPublicSale,
                _publicSaleConfig._maxTokenPerPersonPublicSale <= _maxSupply
            ),
            "Init: Invalid MaxTokenPerPerson of public sale"
        );

        require(_signerAddress != address(0), "Init: Invalid SignerAddress");

        __ERC1155_init(_baseUri);
        __Ownable_init();
        __VRFConsumerBase_init(_randomConfig.vrfCoordinator, _randomConfig.link);

        name = _name;
        symbol = _symbol;
        saleType = _saleType;
        baseUri = _baseUri;
        maxSupply = _maxSupply;
        saleId = _saleId;
        keyHash = _randomConfig.keyHash;
        fee = _randomConfig.fee;

        publicSaleMintCost = _publicSaleConfig._publicSaleMintCost;
        publicSaleStartTime = _publicSaleConfig._publicSaleStartTime;
        publicSaleEndTime = publicSaleStartTime + _publicSaleConfig._publicSaleDuration;
        maxTokenPerMintPublicSale = _publicSaleConfig._maxTokenPerMintPublicSale;
        maxTokenPerPersonPublicSale = _publicSaleConfig._maxTokenPerPersonPublicSale;
        signerAddress = _signerAddress;

        version = 1;
        factory = msg.sender;
    }

    /**
    @notice This function is used to reveal the token can only be called by owner  
    @dev TokensRevealed and URI event is emitted  
    */
    function revealTokens(string memory _uri) external onlyOwner publicSaleEnded {
        require(isrequestfulfilled, "random number to be assigned");
        _revealTokens();
        _updateURI(_uri);
    }

    /**
    @notice This function is used to get random asset id  
    @return assetID Random assetID  
    */
    function getAssetId(uint256 _tokenID) external view returns (uint256) {
        require(revealed, "reveal the token first");
        return _getAssetId(_tokenID);
    }

    /**
    @notice This function is used to create Airdrop  
    @dev It can only be called by owner  
    @param _list list of addresses  
    @param _shares public Sale Shares in Airdrop 
    */
    function createAirdrop(address[] calldata _list, uint256[2] calldata _shares) external isApproved onlyOwner {
        require(!revealed, "Airdrop: Invalid action after reveal");

        _initiateAirdrop(_list, _shares);
    }

    /**
    @notice This function is used to update version of contract
    @param _version version number
    */
    function setVersion(uint256 _version) external onlyOwner {
        version = _version;
    }
}

contracts/Base/BaseNFTWithoutPresale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../Base/BaseNFTSale.sol";
import "./BaseNFTAirdrop.sol";

contract BaseNFTWithoutPresale is BaseNFTSale, BaseNFTAirdrop {
    struct publicSaleConfig {
        uint256 _publicSaleMintCost;
        uint256 _publicSaleStartTime;
        uint256 _publicSaleDuration;
        uint256 _maxTokenPerMintPublicSale;
        uint256 _maxTokenPerPersonPublicSale;
    }

    modifier isSaleOff() {
        _isSaleOff();
        _;
    }

    /**
    @notice This function is used to update public sale parameters  
    @dev It can only be called by owner  
    @param _publicSaleMintCost Cost to mint one token in pubic sale  
    @param _publicSaleStartTime Buffer time to add between presale and public sale i.e 30 + something...  
    @param _publicSaleDuration Duration for which public sales is live  
    @param _maxTokenPerMintPublicSale Maximum No of tokens that can be purchased by user in single tx in public sale  
    @param _maxTokenPerPersonPublicSale Maximum No of token can be purchased by user as whole in public sale  
    */
    function updatePublicSale(
        uint256 _publicSaleMintCost,
        uint256 _publicSaleStartTime,
        uint256 _publicSaleDuration,
        uint256 _maxTokenPerMintPublicSale,
        uint256 _maxTokenPerPersonPublicSale
    ) external onlyOwner {
        require(
            either(
                both(block.timestamp < publicSaleStartTime, _publicSaleStartTime > block.timestamp),
                publicSaleStartTime == _publicSaleStartTime
            ),
            "Invalid Start Time"
        );
        require(block.timestamp < publicSaleEndTime, "The public sale is ended cannot update");
        require(_publicSaleMintCost > 100, "Invalid Token cost");
        require(_publicSaleDuration > 0, "Public sale duration>0");
        require(
            both(
                _maxTokenPerMintPublicSale > 0,
                both(
                    _maxTokenPerPersonPublicSale <= maxSupply,
                    _maxTokenPerPersonPublicSale >= _maxTokenPerMintPublicSale
                )
            ),
            "Maximum token minted per person/per mint not correct"
        );

        publicSaleMintCost = _publicSaleMintCost;
        publicSaleStartTime = _publicSaleStartTime;
        publicSaleEndTime = publicSaleStartTime + _publicSaleDuration;
        maxTokenPerMintPublicSale = _maxTokenPerMintPublicSale;
        maxTokenPerPersonPublicSale = _maxTokenPerPersonPublicSale;
    }

    /**
    @notice This function is used to buy and mint nft in public sale  
    @param tokenQuantity The token quantity that buyer wants to mint  
    */
    function publicSaleBuy(uint256 tokenQuantity) external payable {
        _publicSaleMint(tokenQuantity);
    }

    /**
    @notice This function is used to buy and mint nft in public sale for affiliation feature  
    @param tokenQuantity The token quantity that buyer wants to mint  
    @param affiliatedUser The affiliated user address  
    @param commission The commission percentage that will be paid to affiliated user  
    @param signature The signature sent by the buyer  
    */
    function publicSaleBuyAffiliated(
        uint256 tokenQuantity,
        address affiliatedUser,
        uint256 commission,
        bytes memory signature
    ) external payable {
        _publicSaleMintAffiliated(tokenQuantity, affiliatedUser, commission, signature);
    }

    /**
    @notice This function is used to give away NFTs 
    @dev This function is called when the public sale is not live 
    @param list a list to addresses which will get airdrop  
    @param shares an array of presale and public sale shares (shares[0] should always be zero in without presale case) 
    */
    function _initiateAirdrop(address[] calldata list, uint256[2] calldata shares) internal isSaleOff {
        require(shares[0] == 0, "Airdrop:Invalid shares");

        _createAirdrop(list, shares);
    }

    /**
    @notice This function is used in modifier to check whether any sale is running or not (reduces code size)  
    */
    function _isSaleOff() internal view {
        require(
            either(block.timestamp < publicSaleStartTime, block.timestamp > publicSaleEndTime),
            "Airdrop:Invalid Time"
        );
    }
}

contracts/Sales/NFTWithoutPresaleWithout.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../Base/BaseNFTWithoutPresale.sol";

contract NFTWithoutPresaleWithoutRandomInstant is BaseNFTWithoutPresale {
    string public saleType;
    uint256 public version;

    /**
    @notice This is initializer function is used to deploy new sale contract  
    @param _name Collection name  
    @param _symbol Collection symbol  
    @param _saleType sale Type
    @param _baseUri Collection Base URI  
    @param _maxSupply Collection max supply  
    @param _saleId backend sale id   
    @param _publicSaleConfig Public sale configuration data  
    @param _signerAddress Whitelist signer address  
    */
    function __NFTWithoutPresaleWithoutRandomInstant_init(
        string memory _name,
        string memory _symbol,
        string memory _saleType,
        string memory _baseUri,
        uint256 _maxSupply,
        string memory _saleId,
        publicSaleConfig memory _publicSaleConfig,
        address _signerAddress
    ) public initializer {
        require(_maxSupply > 0, "Init: Invalid max supply");
        require(_publicSaleConfig._publicSaleMintCost > 100, "Init: Invalid Token cost");
        require(block.timestamp <= _publicSaleConfig._publicSaleStartTime, "Init: Invalid Start Time");
        require(_publicSaleConfig._publicSaleDuration > 0, "Init: sale duration > 0");
        require(_publicSaleConfig._maxTokenPerMintPublicSale > 0, "Init: Maximum token per mint in public sale > 0");
        require(
            both(
                _publicSaleConfig._maxTokenPerPersonPublicSale >= _publicSaleConfig._maxTokenPerMintPublicSale,
                _publicSaleConfig._maxTokenPerPersonPublicSale <= _maxSupply
            ),
            "Init: Invalid MaxTokenPerPerson of public sale"
        );

        __ERC1155_init(_baseUri);
        __Ownable_init();

        name = _name;
        symbol = _symbol;
        saleType = _saleType;
        baseUri = _baseUri;
        maxSupply = _maxSupply;
        saleId = _saleId;

        publicSaleMintCost = _publicSaleConfig._publicSaleMintCost;
        publicSaleStartTime = _publicSaleConfig._publicSaleStartTime;
        publicSaleEndTime = publicSaleStartTime + _publicSaleConfig._publicSaleDuration;
        maxTokenPerMintPublicSale = _publicSaleConfig._maxTokenPerMintPublicSale;
        maxTokenPerPersonPublicSale = _publicSaleConfig._maxTokenPerPersonPublicSale;
        signerAddress = _signerAddress;
        version = 1;
        factory = msg.sender;
    }

    /**
    @notice This function is used to create Airdrop  
    @dev It can only be called by owner  
    @param list list of addresses  
    @param shares public sale shares in Airdrop 
    */

    function createAirdrop(address[] calldata list, uint256[2] calldata shares) external isApproved onlyOwner {
        _initiateAirdrop(list, shares);
    }

    /**
    @notice This function is used to update version of contract
    @param _version version number
    */
    function setVersion(uint256 _version) external onlyOwner {
        version = _version;
    }
}

contracts/NFTFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Sales/NFTWithoutPresaleWithoutRandomInstant.sol";
import "./Sales/NFTWithPresaleWithoutRandomInstant.sol";
import "./Sales/NFTWithoutPresaleWithRandomDelayed.sol";
import "./Sales/NFTWithPresaleWithRandomDelayed.sol";
import "./Interfaces/INFTSale.sol";

contract NFTFactory is Ownable {
    uint256 public version = 1;
    address private nftWithoutPresaleWithoutRandomInstant;
    address private nftWithPresaleWithoutRandomInstant;
    address private nftWithoutPresaleWithRandomDelayed;
    address private nftWithPresaleWithRandomDelayed;

    event SaleCreated(address clone, string saleID);

    event SetDropFee(address indexed nftDrop, address feeWallet, uint256 fee);

    event Approved(address approver, address nftDrop);

    event ImplementationUpdated(address previousImpl, address newImpl, uint256 typeId);

    mapping(string => bool) public isSaleidExists;

    mapping(address => bool) public isDropFeeSet;

    mapping(address => bool) public isDropDeployed;

    NFTWithPresaleWithRandomDelayed.randomConfig internal rConfig;

    constructor(
        address _vrfCoordinator,
        address _link,
        bytes32 _keyHash,
        uint256 _fee,
        address _nftWithoutPresaleWithoutRandomInstant,
        address _nftWithPresaleWithoutRandomInstant,
        address _nftWithoutPresaleWithRandomDelayed,
        address _nftWithPresaleWithRandomDelayed
    ) {
        require(_vrfCoordinator != address(0), "Invalid VRF");

        nftWithoutPresaleWithoutRandomInstant = _nftWithoutPresaleWithoutRandomInstant;
        nftWithPresaleWithoutRandomInstant = _nftWithPresaleWithoutRandomInstant;
        nftWithoutPresaleWithRandomDelayed = _nftWithoutPresaleWithRandomDelayed;
        nftWithPresaleWithRandomDelayed = _nftWithPresaleWithRandomDelayed;

        rConfig.vrfCoordinator = _vrfCoordinator;
        rConfig.link = _link;
        rConfig.keyHash = _keyHash;
        rConfig.fee = _fee;
    }

    /**
    @notice This function is used to deploy new sale contract  
    @param _name Collection name  
    @param _symbol Collection symbol  
    @param _saleCreator Collection creator name
    @param _presaleBaseUri Collection Base URI  
    @param _maxSupply Collection max supply  
    @param _saleId backend sale id   
    @param _publicSaleConfig Public sale configuration data  
    @param _signerAddress Whitelist signer address of presale buyers   
    */
    function deployNFTWithoutPresaleWithoutRandomInstant(
        string memory _name,
        string memory _symbol,
        string memory _saleCreator,
        string memory _presaleBaseUri,
        uint256 _maxSupply,
        string memory _saleId,
        NFTWithoutPresaleWithoutRandomInstant.publicSaleConfig memory _publicSaleConfig,
        address _signerAddress
    ) external {
        require(!isSaleidExists[_saleId], "sale id exists");
        require(_publicSaleConfig._publicSaleStartTime != 0, "invalid sale start time");

        address clone = Clones.clone(nftWithoutPresaleWithoutRandomInstant);
        string memory _saleType = string(abi.encodePacked(_saleCreator, "NFTWithoutPresaleWithoutRandomInstant"));
        NFTWithoutPresaleWithoutRandomInstant(clone).__NFTWithoutPresaleWithoutRandomInstant_init(
            _name,
            _symbol,
            _saleType,
            _presaleBaseUri,
            _maxSupply,
            _saleId,
            _publicSaleConfig,
            _signerAddress
        );
        NFTWithoutPresaleWithoutRandomInstant(clone).transferOwnership(msg.sender);
        isSaleidExists[_saleId] = true;
        isDropDeployed[clone] = true;
        emit SaleCreated(clone, _saleId);
    }

    /**
    @notice This function is used to deploy new sale contract  
    @param _name Collection name  
    @param _symbol Collection symbol 
    @param _saleCreator Collection creator name
    @param _presaleBaseUri Collection Base URI  
    @param _maxSupply Collection max supply  
    @param _saleId backend sale id   
    @param _preSaleConfig Pre Sale configuration data   
    @param _publicSaleConfig Public sale configuration data  
    @param _signerAddress Whitelist signer address of presale buyers   
    */
    function deployNFTWithPresaleWithoutRandomInstant(
        string memory _name,
        string memory _symbol,
        string memory _saleCreator,
        string memory _presaleBaseUri,
        uint256 _maxSupply,
        string memory _saleId,
        NFTWithPresaleWithoutRandomInstant.preSaleConfig memory _preSaleConfig,
        NFTWithPresaleWithoutRandomInstant.publicSaleConfig memory _publicSaleConfig,
        address _signerAddress
    ) external {
        require(!isSaleidExists[_saleId], "sale id exists");
        require(_preSaleConfig._preSaleStartTime != 0, "invalid presale start time");
        address clone = Clones.clone(nftWithPresaleWithoutRandomInstant);
        string memory _saleType = string(abi.encodePacked(_saleCreator, "NFTWithPresaleWithoutRandomInstant"));
        NFTWithPresaleWithoutRandomInstant(clone).__NFTWithPresaleWithoutRandomInstant_init(
            _name,
            _symbol,
            _saleType,
            _presaleBaseUri,
            _maxSupply,
            _saleId,
            _preSaleConfig,
            _publicSaleConfig,
            _signerAddress
        );
        NFTWithPresaleWithoutRandomInstant(clone).transferOwnership(msg.sender);
        isSaleidExists[_saleId] = true;
        isDropDeployed[clone] = true;
        emit SaleCreated(clone, _saleId);
    }

    /**
    @notice This function is used to deploy new sale contract  
    @param _name Collection name  
    @param _symbol Collection symbol
    @param _saleCreator Collection creator name  
    @param _baseUri Collection Base URI  
    @param _maxSupply Collection max supply 
    @param _saleId backend sale id   
    @param _publicSaleConfig Public sale configuration data  
    @param _signerAddress Whitelist signer address of presale buyers
    */

    function deployNFTWithoutPresaleWithRandomdelayed(
        string memory _name,
        string memory _symbol,
        string memory _saleCreator,
        string memory _baseUri,
        uint256 _maxSupply,
        string memory _saleId, // Sould not be duplicated
        NFTWithoutPresaleWithRandomDelayed.publicSaleConfig memory _publicSaleConfig,
        address _signerAddress
    ) external {
        require(!isSaleidExists[_saleId], "sale id exists");
        require(_publicSaleConfig._publicSaleStartTime != 0, "invalid sale start time");

        address clone = Clones.clone(nftWithoutPresaleWithRandomDelayed);
        string memory _saleType = string(abi.encodePacked(_saleCreator, "NFTWithoutPresaleWithRandomdelayed"));
        NFTWithoutPresaleWithRandomDelayed(clone).__NFTWithoutPresaleWithRandomDelayed_init(
            _name,
            _symbol,
            _saleType,
            _baseUri,
            _maxSupply,
            _saleId,
            _publicSaleConfig,
            _signerAddress,
            rConfig
        );
        NFTWithoutPresaleWithRandomDelayed(clone).transferOwnership(msg.sender);
        isSaleidExists[_saleId] = true;
        isDropDeployed[clone] = true;
        emit SaleCreated(clone, _saleId);
    }

    /**
    @notice This function is used to deploy new sale contract  
    @param _name Collection name  
    @param _symbol Collection symbol 
    @param _saleCreator Collection creator name  
    @param _baseUri Collection Base URI  
    @param _maxSupply Collection max supply 
    @param _saleId backend sale id   
    @param _preSaleConfig Pre Sale configuration data 
    @param _publicSaleConfig Public sale configuration data  
    @param _signerAddress Whitelist signer address of presale buyers
    */

    function deployNFTWithPresaleWithRandomdelayed(
        string memory _name,
        string memory _symbol,
        string memory _saleCreator,
        string memory _baseUri,
        uint256 _maxSupply,
        string memory _saleId,
        NFTWithPresaleWithRandomDelayed.preSaleConfig memory _preSaleConfig,
        NFTWithPresaleWithRandomDelayed.publicSaleConfig memory _publicSaleConfig,
        address _signerAddress
    ) external {
        require(!isSaleidExists[_saleId], "sale id exists");
        require(_preSaleConfig._preSaleStartTime != 0, "invalid presale start time");

        address clone = Clones.clone(nftWithPresaleWithRandomDelayed);
        string memory _saleType = string(abi.encodePacked(_saleCreator, "NFTWithPresaleWithRandomdelayed"));
        NFTWithPresaleWithRandomDelayed(clone).__NFTWithPresaleWithRandomDelayed_init(
            _name,
            _symbol,
            _saleType,
            _baseUri,
            _maxSupply,
            _saleId,
            _preSaleConfig,
            _publicSaleConfig,
            _signerAddress,
            rConfig
        );
        NFTWithPresaleWithRandomDelayed(clone).transferOwnership(msg.sender);
        isSaleidExists[_saleId] = true;
        isDropDeployed[clone] = true;
        emit SaleCreated(clone, _saleId);
    }

    /**
    @notice This function is used to set drop fees and feeReceiver wallet for nftdrop
    @param _nftDrop drop address  
    */
    function setDropFee(
        address _nftDrop,
        address _feeWallet,
        uint256 _fee
    ) external onlyOwner {
        require(isDropDeployed[_nftDrop], "nft drop is not deployed");
        require(_fee != 0, "fee should be not be 0");
        require(_fee < 10000, "fee<10000");
        require(_feeWallet != address(0), "invalid fee receiver");
        require(!isDropFeeSet[_nftDrop], "fee details set already");

        INFTSale(_nftDrop).setDropFee(_fee, _feeWallet);

        isDropFeeSet[_nftDrop] = true;

        emit SetDropFee(_nftDrop, _feeWallet, _fee);
    }

    /**
    @notice This function is used to approve deployed nftDrop
    @param _nftDrop drop address  
    */
    function setApproval(address _nftDrop) external onlyOwner {
        require(isDropDeployed[_nftDrop], "nft drop is not deployed");
        require(isDropFeeSet[_nftDrop], "invalid approval stage");

        INFTSale(_nftDrop).setDropApproval();

        emit Approved(msg.sender, _nftDrop);
    }

    /**
    @notice This function is used to update vrfCoordinator address  
    @param _vrfCoordinator Chain Link vrfCoordinator address  
    */
    function setVrfCoordinator(address _vrfCoordinator) external onlyOwner {
        require(_vrfCoordinator != address(0), "Invalid VRF");
        rConfig.vrfCoordinator = _vrfCoordinator;
    }

    /**
    @notice This function is used to update link token address  
    @param _link Chain Link link token address  
    */
    function setLinkTokenAddress(address _link) external onlyOwner {
        require(_link != address(0), "Invalid Link");
        rConfig.link = _link;
    }

    /**
    @notice This function is used to update key hash  
    @param _keyHash Chain Link random key hash  
    */
    function setKeyHash(bytes32 _keyHash) external onlyOwner {
        rConfig.keyHash = _keyHash;
    }

    /**
    @notice This function is used to update fee  
    @param _fee fee
    */
    function setFee(uint256 _fee) external onlyOwner {
        rConfig.fee = _fee;
    }

    /**
    @notice This function is used to update version  
    @param _version version number
    */
    function setVersion(uint256 _version) external onlyOwner {
        version = _version;
    }

    /**
        @notice sets the new implementation
        @dev external function to update the new address of the implementation contract, only callable by owner
        @param newImpl addres of the new implementation contract
        @param typeId implementation type
        @custom:emits implementationUpdated event
    */
    function updateImplementation(address newImpl, uint256 typeId) external onlyOwner {
        address prevImpl;
        require(newImpl != address(0), "invalid new address");
        if (typeId == 0) {
            prevImpl = nftWithoutPresaleWithoutRandomInstant;
            require(prevImpl != newImpl, "same implementation");
            nftWithoutPresaleWithoutRandomInstant = newImpl;
        } else if (typeId == 1) {
            prevImpl = nftWithPresaleWithoutRandomInstant;
            require(prevImpl != newImpl, "same implementation");
            nftWithPresaleWithoutRandomInstant = newImpl;
        } else if (typeId == 2) {
            prevImpl = nftWithoutPresaleWithRandomDelayed;
            require(prevImpl != newImpl, "same implementation");
            nftWithoutPresaleWithRandomDelayed = newImpl;
        } else if (typeId == 3) {
            prevImpl = nftWithPresaleWithRandomDelayed;
            require(prevImpl != newImpl, "same implementation");
            nftWithPresaleWithRandomDelayed = newImpl;
        }
        emit ImplementationUpdated(prevImpl, newImpl, typeId);
    }
}

