pragma solidity 0.8.17;

contract BaseNFTDelayed {
    /// @notice Boolean to check if NFTs are revealed
    bool public revealed;

    /**
    @notice Event when token are revealed  
    @param time Time when token are revealed  
    */
    event TokensRevealed(uint256 time);

    /**
    @notice This function is used to reveal the token can only be called by owner  
    @dev TokensRevealed and URI event is emitted  
    */
    function _revealTokens() internal {
        require(!revealed, "Already revealed");
        revealed = true;
        emit TokensRevealed(block.timestamp);
    }
}
","keccak256":"0xb262e66d869241e20c5c6937d9adbdee68e2ab805937f9b77d1d2b765618c781","license":"MIT"},"contracts/Base/BaseNFTSale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./BaseNFT.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

abstract contract BaseNFTSale is BaseNFT {
    using ECDSA for bytes32;

    /** @notice total mint count of collection by referral */
    uint256 public totalMintReferral;

    /** @notice backend saleid of drop */
    string public saleId;

    /** @notice Cost to mint one token in pubic sale */
    uint256 public publicSaleMintCost;

    /** @notice Maximum No of token can be purchased by user in single tx in public sale  */
    uint256 public maxTokenPerMintPublicSale;

    /** @notice Maximum No of token can be purchased by user in one public sale  */
    uint256 public maxTokenPerPersonPublicSale;

    /** @notice Hash map to keep count of token minted by buyer in public sale */
    mapping(address => uint256) public publicsalerListPurchases;

    /** @notice Hash map to keep count of earnings of affiliated user */
    mapping(address => uint256) public affiliatedUserBalance;

    /** @notice total earnings of all affiliated users */
    uint256 public affiliatedWei;

    /** @notice address of the signer */
    address public signerAddress;

    /** @notice address of the feeReceiver */
    address public feeReceiver;

    /** @notice nftDrop factory address */
    address public factory;

    /** @notice nftDrop fee set by the factory owner or admin*/
    uint256 public dropFee;

    /** @notice nftDrop fee collected */
    uint256 public feesCollected;

    /** @notice a variable for tracking whether the nftDrop is approved by the admin or not */
    uint256 public isDropApproved;

    modifier publicSaleLive() {
        _onlyWhenPublicSaleLive();
        _;
    }

    modifier publicSaleEnded() {
        _onlyWhenPublicSaleEnded();
        _;
    }

    modifier isFactory() {
        require(msg.sender == factory, "!auth");
        _;
    }

    modifier isApproved() {
        require(isDropApproved == uint256(1), "!approved");
        _;
    }

    /**
    @notice Event for uri change  
    @param uri New uri of the collection  
    */
    event URI(string uri);

    /**
    @notice This function is used to buy and mint nft in public sale  
    @param tokenQuantity The token quantity that buyer wants to mint  
    */
    function _publicSaleMint(uint256 tokenQuantity) internal isApproved publicSaleLive {
        verifyTokenQuantity(tokenQuantity);
        _mintTo(msg.sender, tokenQuantity);

        unchecked {
            publicsalerListPurchases[msg.sender] += tokenQuantity;
        }
    }

    /**
    @notice This function is used to buy and mint nft in public sale for affiliation feature  
    @param tokenQuantity The token quantity that buyer wants to mint  
    @param affiliatedUser The affiliated user address  
    @param commission The commission percentage that will be paid to affiliated user  
    @param signature The signature sent by the buyer  
    */
    function _publicSaleMintAffiliated(
        uint256 tokenQuantity,
        address affiliatedUser,
        uint256 commission,
        bytes memory signature
    ) internal isApproved publicSaleLive {
        require(affiliatedUser != address(0), "!user");

        bytes32 hash = hashforPublicSaleAffiliated(msg.sender, affiliatedUser, commission);
        require(matchAddressSigner(hash, signature), "invalid-signature");

        verifyTokenQuantity(tokenQuantity);
        _mintTo(msg.sender, tokenQuantity);
        unchecked {
            totalMintReferral += tokenQuantity;
            uint256 receivedAmount = ((publicSaleMintCost * commission) * tokenQuantity) / 100;
            affiliatedUserBalance[affiliatedUser] += receivedAmount;
            affiliatedWei += receivedAmount;
            publicsalerListPurchases[msg.sender] += tokenQuantity;
        }
    }

    /**
    @notice This function is used to withdraw ether from contract  
    */
    function withdrawWei(uint256 _amount) external onlyOwner nonReentrant {
        require(_amount > 0, "Amount>0");
        uint256 totalDropFee;
        unchecked {
            require(address(this).balance - affiliatedWei >= _amount, "Not enough eth");
            totalDropFee = (_amount * dropFee) / 10000;
        }
        (bool success, ) = msg.sender.call{value: _amount - totalDropFee}("");
        require(success, "Tx failed.");
        if (totalDropFee != 0) {
            (bool flag, ) = payable(feeReceiver).call{value: totalDropFee}("");
            require(flag, "Fee:Tx failed.");
            unchecked {
                feesCollected += totalDropFee;
            }
        }
    }

    /**
    @notice This function is used to withdraw affiliated user ether from contract  
    */
    function withdrawAffiliatedFunds(address[] memory affiliatedUser) external onlyOwner publicSaleEnded nonReentrant {
        checkAffiliatedFunds(affiliatedUser);
        uint256 balance;
        uint256 sum;
        uint256 totalDropFee;
        uint256 _fee;
        for (uint256 i; i < affiliatedUser.length; i = unchecked_inc(i)) {
            balance = affiliatedUserBalance[affiliatedUser[i]];
            unchecked {
                _fee = (balance * dropFee) / 10000;
                affiliatedUserBalance[affiliatedUser[i]] = 0;
            }
            (bool success, ) = msg.sender.call{value: balance - _fee}("");
            require(success, "Tx failed.");
            unchecked {
                sum += balance;
                totalDropFee += _fee;
            }
        }
        unchecked {
            affiliatedWei -= sum;
        }

        if (totalDropFee != 0) {
            (bool flag, ) = payable(feeReceiver).call{value: totalDropFee}("");
            require(flag, "Fee:Tx failed.");
            unchecked {
                feesCollected += totalDropFee;
            }
        }
    }

    /**
    @notice This function is used to transfer affiliated user ether from contract  
    */
    function transferAffiliatedFunds(address[] memory affiliatedUser) external onlyOwner publicSaleEnded {
        checkAffiliatedFunds(affiliatedUser);
        uint256 balance;
        uint256 sum;
        uint256 totalDropFee;
        uint256 _fee;
        for (uint256 i; i < affiliatedUser.length; i = unchecked_inc(i)) {
            balance = affiliatedUserBalance[affiliatedUser[i]];
            unchecked {
                _fee = (balance * dropFee) / 10000;
                affiliatedUserBalance[affiliatedUser[i]] = 0;
            }
            (bool success, ) = affiliatedUser[i].call{value: balance - _fee}("");
            require(success, "Transfer failed.");
            unchecked {
                sum += balance;
                totalDropFee += _fee;
            }
        }
        unchecked {
            affiliatedWei -= sum;
        }

        if (totalDropFee != 0) {
            (bool flag, ) = payable(feeReceiver).call{value: totalDropFee}("");

            require(flag, "Fee:Tx failed.");
            unchecked {
                feesCollected += totalDropFee;
            }
        }
    }

    /**
    @notice This function is used to update signer address  
    */
    function updateSignerAddress(address _signerAddress) external onlyOwner {
        require(_signerAddress != address(0), "!signer");
        signerAddress = _signerAddress;
    }

    /**
    @notice This function is used to update uri  
    */
    function updateURI(string memory _uri) public onlyOwner {
        _updateURI(_uri);
    }

    /**
    @dev can be called from factory contract
    @notice This function is used to set drop fee and fee receiver wallet  
    */
    function setDropFee(uint256 _fee, address _wallet) external isFactory {
        dropFee = _fee;
        feeReceiver = _wallet;
    }

    /**
    @dev can be called from factory contract
    @notice This function is used to approve the nftDrop    */
    function setDropApproval() external isFactory {
        require(isDropApproved == uint256(0), "already approved");
        isDropApproved = uint256(1);
    }

    // ============================ Getter Functions ============================

    /**
    @notice This function is used to check if public sale is started  
    @return bool Return true if public is started or not  
    */
    function isPublicSaleLive() public view returns (bool) {
        return both(block.timestamp >= publicSaleStartTime, block.timestamp <= publicSaleEndTime);
    }

    function _onlyWhenPublicSaleLive() internal view {
        require(isPublicSaleLive(), "Public Sale-not live");
    }

    function _onlyWhenPublicSaleEnded() internal view {
        require(block.timestamp >= publicSaleEndTime, "public sale not yet ended");
    }

    /**
    @notice This function is used to get next tokenId 
    @return uint256 tokenID  
    */
    function getNextToken() internal returns (uint256) {
        require(totalMint <= maxSupply, "exceed max Supply");
        _incrementTokenId();
        return currentTokenId;
    }

    /**
    @dev This function is used to check if each affiliated user does have user balance or not   
    @param affiliatedUser array of affiliation user address  
    */
    function checkAffiliatedFunds(address[] memory affiliatedUser) public view {
        require(both(affiliatedUser.length != 0, affiliatedWei != 0), "Nothing to withdraw");
        for (uint256 i; i < affiliatedUser.length; i = unchecked_inc(i)) {
            string memory errorString = string(abi.encodePacked("No balance to transfer for ", affiliatedUser[i]));
            require(affiliatedUserBalance[affiliatedUser[i]] != 0, errorString);
        }
    }

    /**
    @dev This function is used to verify the whitelisted buyer using signature  
    @param hash The hash message generated by the function hashMessage  
    @param signature The signature sent by the buyer  
    @return boolean value true if the signature is verified else false  
    */
    function matchAddressSigner(bytes32 hash, bytes memory signature) public view returns (bool) {
        return signerAddress == hash.recover(signature);
    }

    /**
    @notice This internal function is used to update URI  
    */
    function _updateURI(string memory _uri) internal {
        _setURI(_uri);
        baseUri = _uri;
        emit URI(_uri);
    }

    /**
    @dev This internal function is used to verify the token quantity entered by buyer 
    @param _tokenQuantity token quantity entered by the buyer 
    */
    function verifyTokenQuantity(uint256 _tokenQuantity) internal {
        unchecked {
            require(both(_tokenQuantity > 0, _tokenQuantity <= maxTokenPerMintPublicSale), "Invalid Token Quantity");
            require((totalMint + _tokenQuantity) <= maxSupply, "exceed max supply.");
            require((publicSaleMintCost * _tokenQuantity) <= msg.value, "pay minimum token price");
            require(
                publicsalerListPurchases[msg.sender] + _tokenQuantity <= maxTokenPerPersonPublicSale,
                "exceed maximum allowed limit"
            );
        }
    }

    /**
    @dev This internal function is used to mint token quantity to receiver 
    @param tokenQuantity token quantity entered by the buyer 
    */
    function _mintTo(address receiver, uint256 tokenQuantity) internal {
        uint256 currentId;

        for (uint256 i; i < tokenQuantity; i = unchecked_inc(i)) {
            currentId = getNextToken();
            totalMint = unchecked_inc(totalMint);
            _mint(receiver, currentId, 1, "0x");
        }
    }

    // ============================ Utility Functions ============================

    /**
    @dev This function is used to generate hash message in case of affiliated buy of public sale 
    @param sender The address of the NFT recipient
    @param affiliatedUser The affiliated user address
    @param commission The commission percentage that will be paid to affiliated user
    @return hash generated by the function
    */
    function hashforPublicSaleAffiliated(
        address sender,
        address affiliatedUser,
        uint256 commission
    ) public view returns (bytes32) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encodePacked(sender, block.chainid, affiliatedUser, commission, saleId, address(this)))
            )
        );
        return hash;
    }

    /**
    @dev This function is used to generate hash message during presale buy with whitelisted address
    @param sender The address of the NFT recipient
    @param tokenQuantity tokenQuantity
    @return hash generated by the function
    */
    function hashforPresale(address sender, uint256 tokenQuantity) public view returns (bytes32) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encodePacked(sender, block.chainid, tokenQuantity, saleId, address(this)))
            )
        );
        return hash;
    }

    /**
    @dev This function is used to generate hash message during presale buy with affiliated link
    @param sender The address of the NFT recipient
    @param tokenQuantity tokenQuantity
    @param affiliatedUser The affiliated user address
    @param commission The commission percentage that will be paid to affiliated user
    @return hash generated by the function
    */
    function hashforPresaleAffiliated(
        address sender,
        uint256 tokenQuantity,
        address affiliatedUser,
        uint256 commission
    ) public view returns (bytes32) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(
                    abi.encodePacked(
                        sender,
                        block.chainid,
                        tokenQuantity,
                        affiliatedUser,
                        commission,
                        saleId,
                        address(this)
                    )
                )
            )
        );
        return hash;
    }
}
","keccak256":"0xd2e10e03487a56e8c2f455eef3fb9222e24ce77cae89f6d0b64d762c98e74da4","license":"MIT"},"contracts/Base/BaseNFTWithPresale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./BaseNFTSale.sol";
import "./BaseNFTAirdrop.sol";

contract BaseNFTWithPresale is BaseNFTSale, BaseNFTAirdrop {
    struct preSaleConfig {
        uint256 _preSaleMintCost;
        uint256 _preSaleStartTime;
        uint256 _preSaleDuration;
        uint256 _maxTokenPerMintPreSale;
        uint256 _limitSupplyInPreSale;
    }

    struct publicSaleConfig {
        uint256 _publicSaleMintCost;
        uint256 _publicSaleBufferDuration;
        uint256 _publicSaleDuration;
        uint256 _maxTokenPerMintPublicSale;
        uint256 _maxTokenPerPersonPublicSale;
    }

    /** @notice Cost to mint one token in presale */
    uint256 public preSaleMintCost;

    /**  @notice Time when presale starts */
    uint256 public preSaleStartTime;

    /** @notice Time at which preSale end, calculated preSaleStartTime + preSaleDuration */
    uint256 public preSaleEndTime;

    /** @notice Default buffer time between presale and public sale, constant 30 seconds */
    uint256 internal constant defaultPublicSaleBufferDuration = 30;

    /** @notice Buffer time to add between presale and public sale i.e 30 + something... */
    uint256 public publicSaleBufferDuration;

    /** @notice Maximum No of token can be purchased by user in single tx in pre sale */
    uint256 public maxTokenPerMintPreSale;

    /** @notice Pre Sale supply limit */
    uint256 public limitSupplyInPreSale;

    /** @notice Hash map to keep count of token minted by buyer in pre sale */
    mapping(address => uint256) public presalerListPurchases;

    modifier preSaleEnded() {
        _onlyWhenPresaleEnded();
        _;
    }

    modifier isSaleOff() {
        _isSaleOff();
        _;
    }

    /**
    @notice This function is used to update presale and public sale parameters  
    @dev It can only be called by owner  
    @param _preSaleMintCost Cost to mint one token in presale   
    @param _preSaleStartTime Time when presale starts  
    @param _preSaleDuration Duration for which presale is live  
    @param _maxTokenPerMintPreSale Maximum No of token can be purchased by user in single tx in pre sale  
    @param _limitSupplyInPreSale Pre Sale supply limit  
    @param _publicSaleMintCost Cost to mint one token in pubic sale  
    @param _publicSaleBufferDuration Buffer time to add between presale and public sale i.e 30 + something...  
    @param _publicSaleDuration Duration for which public sales is live  
    @param _maxTokenPerMintPublicSale Maximum No of token can be purchased by user in single tx in public sale  
    @param _maxTokenPerPersonPublicSale Maximum No of token can be purchased by user as whole in public sale  
    */
    function updatePreSale(
        uint256 _preSaleMintCost,
        uint256 _preSaleStartTime,
        uint256 _preSaleDuration,
        uint256 _maxTokenPerMintPreSale,
        uint256 _limitSupplyInPreSale,
        uint256 _publicSaleMintCost,
        uint256 _publicSaleBufferDuration,
        uint256 _publicSaleDuration,
        uint256 _maxTokenPerMintPublicSale,
        uint256 _maxTokenPerPersonPublicSale
    ) external onlyOwner {
        require(
            either(
                both(block.timestamp < preSaleStartTime, _preSaleStartTime > block.timestamp),
                preSaleStartTime == _preSaleStartTime
            ),
            "Invalid Start Time"
        );

        require(block.timestamp < preSaleEndTime, "presale is ended");

        require(
            both(
                _limitSupplyInPreSale > totalMint - publicSaleAirdropCount,
                _limitSupplyInPreSale <= maxSupply - publicSaleAirdropCount
            ),
            "incorrect presale limit supply"
        );

        require(both(_preSaleMintCost > 100, _publicSaleMintCost > 100), "Token cost> 100 wei");
        require(both(_preSaleDuration > 0, _publicSaleDuration > 0), "sale duration>0");
        require(
            both(_maxTokenPerMintPreSale > 0, _maxTokenPerMintPreSale <= _limitSupplyInPreSale),
            "Invalid maximum token per mint in presale"
        );

        require(_maxTokenPerMintPublicSale > 0, "Maximum token per mint in public sale > 0");

        require(
            both(_maxTokenPerPersonPublicSale <= maxSupply, _maxTokenPerPersonPublicSale >= _maxTokenPerMintPublicSale),
            "Invalid Max Token minted per person in public sale"
        );

        preSaleMintCost = _preSaleMintCost;
        publicSaleMintCost = _publicSaleMintCost;

        maxTokenPerMintPreSale = _maxTokenPerMintPreSale;
        limitSupplyInPreSale = _limitSupplyInPreSale;

        preSaleStartTime = _preSaleStartTime;
        unchecked {
            preSaleEndTime = _preSaleStartTime + _preSaleDuration;

            publicSaleBufferDuration = _publicSaleBufferDuration;
            publicSaleStartTime = preSaleEndTime + defaultPublicSaleBufferDuration + _publicSaleBufferDuration;
            publicSaleEndTime = publicSaleStartTime + _publicSaleDuration;
        }
        maxTokenPerMintPublicSale = _maxTokenPerMintPublicSale;
        maxTokenPerPersonPublicSale = _maxTokenPerPersonPublicSale;
    }

    /**
    @notice This function is used to update public sale parameters  
    @dev It can only be called by owner  
    @param _publicSaleMintCost Cost to mint one token in pubic sale  
    @param _publicSaleBufferDuration Buffer time to add between presale and public sale i.e 30 + something...  
    @param _publicSaleDuration Duration for which public sales is live  
    @param _maxTokenPerMintPublicSale Maximum No of token can be purchased by user in single tx in public sale  
    @param _maxTokenPerPersonPublicSale Maximum No of token can be purchased by user as whole in public sale  
    */
    function updatePublicSale(
        uint256 _publicSaleMintCost,
        uint256 _publicSaleBufferDuration,
        uint256 _publicSaleDuration,
        uint256 _maxTokenPerMintPublicSale,
        uint256 _maxTokenPerPersonPublicSale
    ) external onlyOwner {
        uint256 _publicSaleStartTime;
        unchecked {
            _publicSaleStartTime = preSaleEndTime + defaultPublicSaleBufferDuration + _publicSaleBufferDuration;
        }
        require(
            either(
                both(block.timestamp < publicSaleStartTime, _publicSaleStartTime > block.timestamp),
                publicSaleStartTime == _publicSaleStartTime
            ),
            "Invalid Start Time"
        );
        require(block.timestamp < publicSaleEndTime, "Public sale is ended");
        require(_publicSaleMintCost > 100, "Invalid Token cost");
        require(_publicSaleDuration > 0, "Public sale duration > 0");
        require(
            both(
                _maxTokenPerMintPublicSale > 0,
                both(
                    _maxTokenPerPersonPublicSale <= maxSupply,
                    _maxTokenPerPersonPublicSale >= _maxTokenPerMintPublicSale
                )
            ),
            "Maximum token minted per person/per mint not correct"
        );

        publicSaleMintCost = _publicSaleMintCost;
        publicSaleBufferDuration = _publicSaleBufferDuration;
        publicSaleStartTime = _publicSaleStartTime;
        publicSaleEndTime = publicSaleStartTime + _publicSaleDuration;
        maxTokenPerMintPublicSale = _maxTokenPerMintPublicSale;
        maxTokenPerPersonPublicSale = _maxTokenPerPersonPublicSale;
    }

    /**
    @notice This function is used to buy and mint nft in presale  
    @dev Random token id is generated for assigned to buyer  
    @param tokenSignQuantity The token quantity that whitelisted buyer can mint  
    @param tokenQuantity The token quantity that whitelisted buyer wants to mint  
    @param signature The signature sent by the buyer  
    */
    function preSaleBuy(
        uint256 tokenSignQuantity,
        uint256 tokenQuantity,
        bytes memory signature
    ) external payable isApproved {
        require(isPreSaleLive(), "presale is not live");

        bytes32 hash = hashforPresale(msg.sender, tokenSignQuantity);

        require(matchAddressSigner(hash, signature), "invalid-signature");

        verifyTokenQtyInPresale(tokenQuantity);

        unchecked {
            require(tokenSignQuantity != 0, "TokenSignQuantity > 0");
            require(
                presalerListPurchases[msg.sender] + tokenQuantity <= tokenSignQuantity,
                "exceeds maximum allowed limit"
            );

            require((preSaleMintCost * tokenQuantity) <= msg.value, "insufficient Amount paid");
        }

        _mintTo(msg.sender, tokenQuantity);

        unchecked {
            presalerListPurchases[msg.sender] += tokenQuantity;
        }
    }

    /**
    @notice This function is used to buy and mint nft in public sale  
    @param tokenQuantity The token quantity that buyer wants to mint  
    */
    function publicSaleBuy(uint256 tokenQuantity) public payable preSaleEnded {
        _publicSaleMint(tokenQuantity);
    }

    /**
    @notice This function is used to buy and mint nft in presale   
    @dev Random token id is generated for assigned to buyer  
    @param tokenSignQuantity The token quantity that whitelisted buyer can mint   
    @param tokenQuantity The token quantity that whitelisted buyer wants to mint   
    @param affiliatedUser The affiliated user address  
    @param commission The commission percentage that will be paid to affiliated user   
    @param signature The signature sent by the buyer   
    */
    function preSaleBuyAffiliated(
        uint256 tokenSignQuantity,
        uint256 tokenQuantity,
        address affiliatedUser,
        uint256 commission,
        bytes memory signature
    ) external payable isApproved {
        require(isPreSaleLive(), "presale is not live");
        require(affiliatedUser != address(0), "Invalid Affiliated user");

        bytes32 hash = hashforPresaleAffiliated(msg.sender, tokenSignQuantity, affiliatedUser, commission);

        require(matchAddressSigner(hash, signature), "invalid-signature");

        verifyTokenQtyInPresale(tokenQuantity);

        unchecked {
            require(tokenSignQuantity != 0, "TokenSignQuantity > 0");
            require(
                presalerListPurchases[msg.sender] + tokenQuantity <= tokenSignQuantity,
                "exceeds maximum allowed limit"
            );

            require((preSaleMintCost * tokenQuantity) <= msg.value, "insufficient amount paid");
        }

        _mintTo(msg.sender, tokenQuantity);

        unchecked {
            totalMintReferral += tokenQuantity;
            presalerListPurchases[msg.sender] += tokenQuantity;
            uint256 receivedAmount = ((preSaleMintCost * commission) * tokenQuantity) / 100;
            affiliatedUserBalance[affiliatedUser] += receivedAmount;
            affiliatedWei += receivedAmount;
        }
    }

    /**
    @notice This function is used to buy and mint nft in public sale for affiliation feature  
    @param tokenQuantity The token quantity that buyer wants to mint  
    @param affiliatedUser The affiliated user address  
    @param commission The commission percentage that will be paid to affiliated user  
    @param signature The signature sent by the buyer  
    */
    function publicSaleBuyAffiliated(
        uint256 tokenQuantity,
        address affiliatedUser,
        uint256 commission,
        bytes memory signature
    ) public payable preSaleEnded {
        require(affiliatedUser != address(0), "Invalid address");
        _publicSaleMintAffiliated(tokenQuantity, affiliatedUser, commission, signature);
    }

    /**
    @notice This function is used to perform Airdrop operation  
    @dev This function is called by only owner when presale and public sales are not live 
    @param list a list to addresses which will get airdrop  
    @param shares an array of presale and public sale shares 
    */
    function _initiateAirdrop(address[] calldata list, uint256[2] calldata shares) internal isSaleOff {
        if (isPreSaleStarted()) {
            require(shares[0] == 0, "Airdrop: Invalid Presale Share");
        }

        require(shares[0] + preSaleAirdropCount <= limitSupplyInPreSale, "Airdrop: Presale share not in range");

        _createAirdrop(list, shares);

        unchecked {
            preSaleAirdropCount += shares[0];
            publicSaleAirdropCount += shares[1];
        }
    }

    /**
    @dev This function is used to verify the token quantity entered by buyer 
    @param _tokenQuantity token quantity entered by the buyer 
    */
    function verifyTokenQtyInPresale(uint256 _tokenQuantity) private view {
        require(both(_tokenQuantity > 0, _tokenQuantity <= maxTokenPerMintPreSale), "Invalid Token Quantity");
        unchecked {
            require(
                _tokenQuantity + totalMint <= limitSupplyInPreSale + publicSaleAirdropCount,
                "exceeding presale supply"
            );
        }
    }

    // ============================ Getter Functions ============================

    /**
    @notice This function is used to check if pre sale is started  
    @return bool Return true if presale is started or not  
    */
    function isPreSaleLive() public view returns (bool) {
        return both(block.timestamp >= preSaleStartTime, block.timestamp <= preSaleEndTime);
    }

    /**
    @notice This function is used to check whether the presale has already started or happened  
    */
    function isPreSaleStarted() private view returns (bool) {
        return block.timestamp >= preSaleStartTime;
    }

    /**
    @notice This function is used in modifier to check whether any sale is running or not (reduces code size)  
    */
    function _isSaleOff() internal view {
        require(
            both(
                either(block.timestamp < publicSaleStartTime, block.timestamp > publicSaleEndTime),
                either(block.timestamp < preSaleStartTime, block.timestamp > preSaleEndTime)
            ),
            "Airdrop:Invalid Time"
        );
    }

    /**
    @notice This function is used in modifier to check whether presale is ended or not (reduces code size)  
    */
    function _onlyWhenPresaleEnded() internal view {
        require(block.timestamp > preSaleEndTime, "the pre-sale is not yet ended");
    }
}
